import React from 'react';

import Card from '../components/Card';

import devgrub from '../assets/images/devgrub.png';
import youtube from '../assets/images/youtube.png';
import amazonreviews from '../assets/images/amazonreviews.jpeg';
import senti from '../assets/images/senti.jpg';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

class Carousel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    title: 'Project 1',
                    subTitle: 'The cookbook for developers',
                    imgSrc: devgrub,
                    // link: 'https://devgrub.com',
                    selected: false
                },
                {
                    id: 1,
                    title: 'Project 2',
                    subTitle: 'YouTube channel',
                    imgSrc: youtube,
                    // link: 'https://www.youtube.com/channel/UCxSITxL2JbF229OGCqieVZw',
                    selected: false
                },
                {
                    id: 2,
                    title: 'Sentiment Analysis',
                    imgSrc: amazonreviews,
                    imgSrc: senti,
                    subtitle: 'Each Amazon review, along with text has a user rating ranging from 1 to 5 stars. Commonly, reviews on Amazon’s products rate the product 4 or 5 stars , and such reviews are almost always considered helpful. 1-stars are used to signify disapproval, and 2-star and 3-stars reviews have no significant impact at all. Then, what is the whole point of having a rating scale of 5 at all if the vast majority of reviewers favor the product?',
                    text: "For the given dataset, sentiment analysis performed on reviews with 2,3,4 stars has given the following results:- Sentiment Percentage Distribution. - Negative: 50 - Neutral: 50%",
                    // link: 'https://github.com/garrettlove8/evverest',
                    selected: false
                },
            ]
        }
    }


    handleCardClick = (id, card) => {

        let items = [...this.state.items];

        items[id].selected = items[id].selected ? false : true;

        items.forEach(item => {
            if(item.id !== id) {
                item.selected = false;
            }
        });

        this.setState({
            items
        });
    }


    makeItems = (items) => {
        return items.map(item => {
            return <Card item={item} click={(e => this.handleCardClick(item.id, e))} key={item.id} />
        })
    }


    render() {
        return(
            <Container fluid={true}>
                <Row className="justify-content-around">
                    {this.makeItems(this.state.items)}
                </Row>
            </Container>
        );
    }

}

export default Carousel;
