"use strict"; // Super inefficient, needs to be cached.

module.exports = function (lineColumn, text) {
  var index = 0;

  for (var i = 0; i < lineColumn.line - 1; ++i) {
    index = text.indexOf("\n", index) + 1;

    if (index === -1) {
      return -1;
    }
  }

  return index + lineColumn.column;
};